import { Component, Input, OnInit, ViewChild } from '@angular/core';
import { NgForm } from '@angular/forms';
import { Router } from '@angular/router';
import { Cidade } from '../../shared/models/cidade.model';
import { Endereco } from '../../shared/models/endereco.model';
import { Instituicao } from '../../shared/models/instituicao.model';
import { CidadeService } from '../../shared/services/cidade.service';
import { InstituicaoService } from '../../shared/services/instituicao.service';

@Component({
  selector: 'app-inserir-instituicao',
  templateUrl: './inserir-instituicao.component.html',
  styleUrls: ['./inserir-instituicao.component.css']
})
export class InserirInstituicaoComponent implements OnInit {

  @ViewChild('formInstituicao') formInstituicao!: NgForm;

  @Input()
  cidades: Cidade[];

  instituicao!: Instituicao;
  cidade: Cidade;

  constructor(
    private service: InstituicaoService,
    private cidadeService: CidadeService,
    private router: Router
  ) {
    this.cidade = new Cidade();
    this.instituicao = new Instituicao();
    this.instituicao.endereco = new Endereco();
    this.cidades = [];
  }

  async ngOnInit() {
    await this.buscarCidades();
  }

  private async buscarCidades() {
    this.cidades = (await this.cidadeService.BuscarCidades().toPromise())!;
  }

  async inserir(): Promise<void> {
    if (this.formInstituicao.form.valid) {
      this.instituicao.endereco!.cidadeId = this.cidade.id;
      this.instituicao.endereco!.cidade = this.cidade;
      await this.service.inserir(this.instituicao).toPromise();
      this.router.navigate(["/instituicoes"]);
    }
  }
}
<h1>Inserir Instituição</h1>
<form #formInstituicao="ngForm">
  <div class="row">
    <div class="col-md-4">
      <div class="form-group">
        <label for="nome">Nome</label>
        <input type="text" class="form-control" id="nome" name="nome" required [(ngModel)]="instituicao.nome" #nome="ngModel">
        <div *ngIf="nome.errors && (nome.dirty || nome.touched)" class="alert alert-danger">
          <div [hidden]="!nome.errors?.['required']">Digite o nome.</div>
        </div>
      </div>
    </div>
    <div class="col-md-4">
      <div class="form-group">
        <label for="email">Email</label>
        <input type="text" class="form-control" id="email" name="email" required [(ngModel)]="instituicao.email" #email="ngModel">
        <div *ngIf="email.errors && (email.dirty || email.touched)" class="alert alert-danger">
          <div [hidden]="!email.errors?.['required']">Digiteo o email.</div>
        </div>
      </div>
    </div>
    <div class="col-md-4">
      <div class="form-group">
        <label for="site">Site</label>
        <input type="text" class="form-control" id="site" name="site" required [(ngModel)]="instituicao.site" #site="ngModel">
        <div *ngIf="site.errors && (site.dirty || site.touched)" class="alert alert-danger">
          <div [hidden]="!site.errors?.['required']">Digiteo o site.</div>
        </div>
      </div>
    </div>
  </div>
  <div class="row">
    <div class="col-md-12">
      <div class="form-group">
        <label for="descricao">Descricao</label>
        <textarea class="form-control" id="descricao" rows="4" cols="50" maxlength="600" name="descricao" required [(ngModel)]="instituicao.descricao" #descricao="ngModel"></textarea>
        <div *ngIf="descricao.errors && (descricao.dirty || descricao.touched)" class="alert alert-danger">
          <div [hidden]="!descricao.errors?.['required']">Digite a descrição.</div>
        </div>
      </div>
    </div>
  </div>
  <div class="row">
    <div class="col-md-8">
      <div class="form-group">
        <label for="logradouro">Logradouro</label>
        <input type="text" class="form-control" id="logradouro" name="logradouro" required [(ngModel)]="instituicao.endereco!.logradouro" #logradouro="ngModel">
        <div *ngIf="logradouro.errors && (logradouro.dirty || logradouro.touched)" class="alert alert-danger">
          <div [hidden]="!logradouro.errors?.['required']">Digiteo o logradouro.</div>
        </div>
      </div>
    </div>
    <div class="col-md-4">
      <div class="form-group">
        <label for="logradouro">Número</label>
        <input type="number" class="form-control" id="numero" name="numero" required [(ngModel)]="instituicao.endereco!.numero" #numero="ngModel">
      </div>
    </div>
  </div>
  <div class="row">
    <div class="col-md-6">
      <div class="form-group">
        <label for="cidades">Cidade</label>
        <ng-select name="cidades" bindvalue="id" [items]="cidades" bindLabel="descricaoFormatada" [(ngModel)]="cidade" class="form-control"></ng-select>
      </div>
    </div>
    <div class="col-md-6">
      <div class="form-group">
        <label for="bairro">Bairro</label>
        <input type="text" class="form-control" id="bairro" name="bairro" required [(ngModel)]="instituicao.endereco!.bairro" #bairro="ngModel">
        <div *ngIf="bairro.errors && (bairro.dirty || bairro.touched)" class="alert alert-danger">
          <div [hidden]="!bairro.errors?.['required']">Digiteo o bairro.</div>
        </div>
      </div>
    </div>
  </div>
  <div class="row">
    <div class="col-md-12">
      <div class="form-group">
        <button type="button" class="btn btn-primary" (click)="inserir()" [disabled]="!formInstituicao.form.valid">
          <i class="fa fa-save" aria-hidden="true"></i> Salvar
        </button>
        <a class="btn btn-secondary" [routerLink]="['/instituicoes']">
          <i class="fa" aria-hidden="true"></i> Voltar
        </a>
      </div>
    </div>
  </div>
</form>
